7public class SmsApp {
8    public static void main(String[] args) {
9        get("/", (req, res) -> "Hello Web");
10
11        post("/sms", (req, res) -> {
12            res.type("application/xml");
13            Body body = new Body
14                    .Builder("The Robots are coming! Head for the hills!")
15                    .build();
16            Message sms = new Message
17                    .Builder()
18                    .body(body)
19                    .build();
20            MessagingResponse twiml = new MessagingResponse
21                    .Builder()
22                    .message(sms)
23                    .build();
24            return twiml.toXml();
25        });
26    }
